import React from 'react'
import ReactDOM from 'react-dom'

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]
  console.log(parts[0])

  return (
    <div>
      <Header name={course}/>

      <Content name={part1.name} exercise={parts[0].part1.exercises}/>
      <Content name={part2.name} exercise={parts[0].part2.exercises}/>
      <Content name={part3.name} exercise={parts[0].part3.exercises}/>

      <Footer exercises1={part1.exercises} exercises2={part2.exercises} exercises3={part3.exercises} />
    </div>
  )
}

