import { render } from '@testing-library/react'
import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const all = (good + bad + neutral);
  const total = (good - bad);

  return (
    <div>
      <h1>give feedback</h1>
      <Buttons good={good} neutral={neutral} bad={bad} setGood={setGood} setNeutral={setNeutral} setBad={setBad} />

      <h1>statistics</h1>
      <FooterStats good={good} neutral={neutral} bad={bad} />
      <NewFooterStats all={all} total={total} good={good}/>

    </div>
  )
}

const Button = (props) => (
  <button onClick={props.handleClick}>
    {props.title}
  </button>
)

const FooterStat = (props) => (
  <p>{props.title} {props.stats}</p>
)

const FooterStats = (props) => {
  return (
    <div>
      <FooterStat title="good" stats={props.good} />
      <FooterStat title="neutral" stats={props.neutral} />
      <FooterStat title="bad" stats={props.bad} />
    </div>
  )
}

const Buttons = (props) => {
  const incrementValue = (newValue, eventHandler) => {

    eventHandler(newValue + 1)
  };

  return (
    <div>
      <Button title={"good"} handleClick={() => incrementValue(props.good, props.setGood)} />
      <Button title={"neutral"} handleClick={() => incrementValue(props.neutral, props.setNeutral)} />
      <Button title={"bad"} handleClick={() => incrementValue(props.bad, props.setBad)} />
    </div>)
}

const NewFooterStat = (props) => {
  if (props.value === 0) {
    return (
      <p>No feedback given</p>
    )
  } else if (props.value === null) {
    return (null)
  }
  else {
    return (
      <p>{props.title} {props.value}</p>
    )
  }
}

const NewFooterStats = (props) => {
  return(
    <div>
      <NewFooterStat title="all" value={props.all} />
      <NewFooterStat title="average" value={(props.all) ? (props.total / props.all) + "" : null} />
      <NewFooterStat title="positive" value={(props.all) ? (props.good / props.all) * 100 + "%" : null} />

    </div>
  )
}

ReactDOM.render(<App />,
  document.getElementById('root')
)
