import { render } from '@testing-library/react'
import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const incrementValue = (newValue, eventHandler) => {

    eventHandler(newValue + 1)
  };

  const all = (good + bad + neutral);
  const total = (good - bad);

  return (
    <div>
      <h1>give feedback</h1>
      <Buttons title={"good"} handleClick={() => incrementValue(good, setGood)} />

      <Buttons title={"neutral"} handleClick={() => incrementValue(neutral, setNeutral)} />

      <Buttons title={"bad"} handleClick={() => incrementValue(bad, setBad)} />

      <h1>statistics</h1>
      <FooterStats good={good} neutral={neutral} bad={bad} />

      <NewFooterStats title="all" value={all} />
      <NewFooterStats title="average" value={(all) ? (total / all) + "" : null} />

      <NewFooterStats title="positive" value={(all) ? (good / all) * 100 + "%" : null} />

    </div>
  )
}

const FooterStats = (props) => {
  return(
    <div>
      <FooterStat title="good" stats={props.good} />
      <FooterStat title="neutral" stats={props.neutral} />
      <FooterStat title="bad" stats={props.bad} />
    </div>
  )
}

const Button = (props) => (
  <button onClick={props.handleClick}>
    {props.title}
  </button>
)

const Buttons = (props) => (
  <button onClick={props.handleClick}>
    {props.title}
  </button>
)

const FooterStat = (props) => (
  <p>{props.title} {props.stats}</p>
)

const NewFooterStats = (props) => {
  if (props.value === 0) {
    return (
      <p>No feedback given</p>
    )
  } else if (props.value === null) {
    return(null)
  }
  else {
    return (
      <p>{props.title} {props.value}</p>
    )
  }
}

ReactDOM.render(<App />,
  document.getElementById('root')
)
