import React from 'react'
import ReactDOM from 'react-dom'

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]
  console.log(parts[0].name);

  return (
    <div>
      <Header name={course}/>

      <Content name={parts[0].name} exercise={parts[0].exercises}/>
      <Content name={parts[1].name} exercise={parts[1].exercises}/>
      <Content name={parts[2].name} exercise={parts[2].exercises}/>

      <Footer exercises1={parts[0].exercises} exercises2={parts[1].exercises} exercises3={parts[2].exercises} />
    </div>
  )
}

const Header = (course) => {
  return(
    <div>
  <h1>{course.name}</h1>
  </div>
  )
}
const Content = (content) => {
  return(
    <div>
      <p>{content.name} {content.exercise}</p>
    </div>
  )
}
const Footer = (footerContent) => {
  return(
    <div>
      <p>Number of exercises {footerContent.exercises1 + footerContent.exercises2 + footerContent.exercises3} </p>
    </div>
  )
}



ReactDOM.render(<App />, document.getElementById('root'))
