import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const incrementValue = (newValue, eventHandler) => {
    eventHandler(newValue + 1)
  };

  const showStatistics = (dataCollected) => {
    eventHandler(newValue + 1)
  };

  const all = (good + bad + neutral);
  const total = (good - bad);

  return (
    <div>
      <h1>give feedback</h1>
      <Button title={"good"} handleClick={() => incrementValue(good, setGood)}/>

      <Button title={"neutral"} handleClick={() => incrementValue(neutral, setNeutral)}/>

      <Button title={"bad"} handleClick={() => 
        incrementValue(bad, setBad), showStatistics(true)
        }/>

      <h1>statistics</h1>
      <FooterStats title="good" stats={good} />
      <FooterStats title="neutral" stats={neutral} />
      <FooterStats title="bad" stats={bad} />

      <NewFooterStats title="all" value={ all } />
      <NewFooterStats title="average" value={ total / all } />
      <NewFooterStats title="positive" value= { (good / all) * 100 + "%" } />

    </div>
  )
}

const Button = (props) => (
  <button onClick={props.handleClick}>
    {props.title}
    </button>
) 

const FooterStats = (props) => (
  <p>{props.title} {props.stats}</p>
)

const NewFooterStats = (props) => (
<p>{props.title} {props.value}</p>
)

ReactDOM.render(<App />, 
  document.getElementById('root')
)
